import React from 'react';
import './App.css'
import {Route,Switch,Redirect} from "react-router-dom";
import Header from "./componet/Header";
import BooksContext from './context/BooksContext';
import AddBook from "./componet/AddBook";
import BookList from "./componet/BookList";
import EditBook from "./componet/EditBook";
import useLocalStorage from './hooks/useLocalStroge'
function App() {
    const [books, setBooks] = useLocalStorage('books', []);
    return (
        <div>
            <Header />
            <div className="main-content">
                <Switch>
                    <Route
                        render={(props) => (
                            <BookList {...props} books={books} setBooks={setBooks} />
                        )}
                        path="/"
                        exact={true}
                    />
                    <Route
                        render={(props) => (
                            <AddBook {...props} books={books} setBooks={setBooks} />
                        )}
                        path="/add"
                    />
                    <Route
                        render={(props) => (
                            <EditBook {...props} books={books} setBooks={setBooks} />
                        )}
                        path="/edit/:id"
                    />
                    <Route component={() => <Redirect to="/" />} />
                </Switch>
            </div>
        </div>
    );
}

export default App;
